'From Pharo4.0 of 18 March 2013 [Latest update: #40626] on 29 October 2016 at 11:32:10.711935 am'!PLC_EditableTagMorph subclass: #PLC_ActualArgumentMorph	instanceVariableNames: 'model '	classVariableNames: ''	poolDictionaries: ''	category: 'IDE4PLC-GraphicElementMorph'!!PLC_TagMorph methodsFor: 'drawing' stamp: 'CarlosLombardi 10/28/2016 22:37'!textColor	^ self userInteractionStateLayer1 textColor! !!PLC_TagMorph methodsFor: 'drawing' stamp: 'CarlosLombardi 10/28/2016 22:37'!drawElementOn: aCanvas	"Dibujo el texto"	| strings |	strings := self stringsToDraw.	strings		do: [ :str | 			"Linea de texto"			aCanvas				drawString: str				at: (self stringPositionFor: str index: (strings indexOf: str))				font: TextStyle defaultFont				color: self textColor.				"Borde delimitador de la linea de texto"			self properties drawTextLineBorder				ifTrue: [ 					self						drawTextLineBorderOn: aCanvas						rectangle:							((self stringPositionFor: str index: (strings indexOf: str))								corner: (self stringPositionFor: str index: (strings indexOf: str)) + str asMorph extent) ] ]! !!PLC_ActualArgumentMorph methodsFor: 'drawing' stamp: 'CarlosLombardi 10/29/2016 11:25'!textColor	"the color in which the text of this morph, i.e. the identifier	 of my model, must be written.	 Redefined in order to handle error arguments"	| standardColor errorColor |	standardColor := super textColor.	errorColor := (standardColor = Color white) 		ifTrue: [ Color orange ] 		ifFalse: [ Color red ] .	^(self element notNil and: [ self element actualArgument isError ])		ifTrue: [ errorColor ]		ifFalse: [ standardColor ]! !!PLC_ActualArgument methodsFor: 'graphic element' stamp: 'CarlosLombardi 10/29/2016 11:25'!graphicElementFor: aConnectableBlock	"Devuelve el Elemento grafico (Morph)."	| argumentMorph |	argumentMorph := PLC_ActualArgumentMorph		newWithText: self actualArgument identifier		andContainer: aConnectableBlock connectableBlocksNetwork controller view.	^ argumentMorph			"	M = PLC_ConnectableBlocksNetwork	V = PLC_LadderView	C = PLC_LadderController	"! !PLC_ActualArgumentMorph removeSelector: #model!PLC_ActualArgumentMorph removeSelector: #model1!PLC_ActualArgumentMorph removeSelector: #model:!PLC_EditableTagMorph subclass: #PLC_ActualArgumentMorph	instanceVariableNames: 'model'	classVariableNames: ''	poolDictionaries: ''	category: 'IDE4PLC-GraphicElementMorph'!!PLC_ActualArgumentMorph reorganize!(#drawing textColor)(#extent layoutRectangleExtent leftLayoutRectangleExtent rightLayoutRectangleExtent centerLayoutRectangleExtent)(#'draw elements' outputPinsSize)(#'model exploration' addToGroup:)(#layout xPositionedCascade: layoutOn:from:output:)(#position layoutRectanglePosition leftLayoutRectanglePosition centerLayoutRectanglePosition rightLayoutRectanglePosition)(#accessing)!